import "./Costs.css";
import CostItem from "./CostItem";
import Card from "../UI/Card";
import CostsFilter from "./CostFilter";
import React, { useState } from "react";

function Cost(props) {
  const [selectYear, setSelectYear] = useState("2023");

  const changeYear = (year) => {
    setSelectYear(year);
  };

  const filteredCost = props.costs.filter((cost) => {
    return cost.date.getFullYear().toString() === selectYear;
  });

  const costsContent = <p>В этом году расходов нет</p>;

  if (filteredCost.length > 0) {
    costsContent = filteredCost.map((cost) => (
      <CostItem
        key={cost.id}
        date={cost.date}
        description={cost.description}
        amount={cost.amount}
      />
    ));
  }

  return (
    <div>
      <Card className="costs">
        <CostsFilter year={selectYear} onChangeYear={changeYear} />
        {costsContent}
      </Card>
    </div>
  );
}

export default Cost;
